import matplotlib.pyplot as pltimport numpy as npfrom sys import stdoutfrom models import Henonfrom mpmath import mpmp.dps = 30####  Фазовый портрет динамической системы###a = mp.mpf('1.43')b = mp.mpf('0.5')c = mp.mpf('-1.759414883')x = [mp.mpf('0.1'), mp.mpf('0.1'), mp.mpf('0.1')]fase_port_x_0 = []fase_port_y_0 = []fase_port_z_0 = []fase_port_x_1 = []fase_port_y_1 = []fase_port_z_1 = []fase_port_x_2 = []fase_port_y_2 = []fase_port_z_2 = []fase_port_x_3 = []fase_port_y_3 = []fase_port_z_3 = []for i in range(1000):    x = Henon(x, a, b, c)n = 0M = 0k = 0l = 0while l != 10_000:    stdout.write("\r%d" % l)    stdout.flush()    x = Henon(x, a, b, c)    fase_port_x_0.append(x[0])    fase_port_y_0.append(x[1])    fase_port_z_0.append(x[2])    n += 1    if (-0.41 < x[0] < -0.39) and (-0.406 < x[1] < -0.39):        fase_port_x_1.append(x[0])        fase_port_y_1.append(x[1])        fase_port_z_1.append(x[2])        M += 1    if (-0.4008 < x[0] < -0.4) and (-0.3978 < x[1] < -0.3971):        fase_port_x_2.append(x[0])        fase_port_y_2.append(x[1])        fase_port_z_2.append(x[2])        k += 1    if (-0.40074 < x[0] < -0.4007) and (-0.39715 < x[1] < -0.39711):        fase_port_x_3.append(x[0])        fase_port_y_3.append(x[1])        fase_port_z_3.append(x[2])        l += 1print(' ')print(n)print(M)print(k)print(l)fig, ax0 = plt.subplots()fig, ax1 = plt.subplots()fig, ax2 = plt.subplots()fig, ax3 = plt.subplots()ax0.plot(fase_port_x_0, fase_port_y_0, 'g.', ms=0.1)ax1.plot(fase_port_x_1, fase_port_y_1, 'g.', ms=0.3)ax2.plot(fase_port_x_2, fase_port_y_2, 'g.', ms=0.3)ax3.plot(fase_port_x_3, fase_port_y_3, 'g.', ms=0.3)ax0.set_title(f'A = {a}, B = {b}, C = {c}')ax0.grid()ax0.set_xlabel('X')ax0.set_ylabel('Y')ax1.set_title(f'A = {a}, B = {b}, C = {c}')ax1.grid()ax1.set_xlabel('X')ax1.set_ylabel('Y')ax2.set_title(f'A = {a}, B = {b}, C = {c}')ax2.grid()ax2.set_xlabel('X')ax2.set_ylabel('Y')ax3.set_title(f'A = {a}, B = {b}, C = {c}')ax3.grid()ax3.set_xlabel('X')ax3.set_ylabel('Y')plt.show()